<script>
	export let point;

	let schedule = 'Abierto todo el día'
	$: if (point.schedule) {
		schedule = ''
		for (let i = 0; i < point.schedule.length; i++) {
			schedule += point.schedule[i][0]
			schedule += ' - '
			schedule += point.schedule[i][1]
			if (i != point.schedule.length - 1)
				schedule += '&nbsp;&nbsp;|&nbsp;&nbsp;'
		}
	}

</script>

